def _render(html, fn=None):
    if fn is not None:
        return fn(html, unsafe_allow_html=True)
    else:
        return html


def card(title, data, fn=None):
    """
    Render description list element representing a summary card with given
    title and data.
    """
    return _render(f'<dl class="card-box"><dt>{title}</dt><dd>{data}</dd></dl>', fn)


def cards(entries, fn=None):
    """
    Renders mapping as a list of cards.
    """
    raw = "".join(card(k, v) for k, v in entries)
    return _render(f"""<div class="card-boxes">{raw}</div>""", fn)


def icon(title, subtitle, fn=None):
    html = f"""
    <div id="sidebar-icon">
    <img src="data:image/svg+xml;base64,
    PD94bWwgdmVyc2lvbj0iMS4wIiA
    ">
    <span>{title}<br>{subtitle}</span>
    </div>
    """
    return _render(html, fn)
